{
  "name": "OursGPT",
  "description": "A premium, futuristic AI chatbot and image generation platform inspired by ChatGPT, powered by Gemini.",
  "requestFramePermissions": [
    "camera",
    "microphone"
  ]
}

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export type Role = 'user' | 'model';

export interface MessagePart {
  text?: string;
  inlineData?: {
    mimeType: string;
    data: string;
  };
}

export interface Message {
  id: string;
  role: Role;
  content: string;
  timestamp: number;
  image?: {
    data: string;
    mimeType: string;
  };
}

export interface ChatThread {
  id: string;
  title: string;
  messages: Message[];
  updatedAt: number;
}

export type ImageStyle = 'realistic' | 'anime' | 'cinematic' | '3D' | 'illustration';

export interface GeneratedImage {
  id: string;
  url: string;
  prompt: string;
  style: ImageStyle;
  timestamp: number;
}

export enum AppRoute {
  CHAT = 'chat',
  IMAGE_GEN = 'image-gen',
  ABOUT = 'about',
  PRIVACY = 'privacy',
  SETTINGS = 'settings'
}

import { GoogleGenAI, GenerateContentResponse } from "@google/genai";
import { Message, ImageStyle } from "../types";

export const geminiService = {
  async chat(messages: Message[], systemInstruction?: string): Promise<string> {
    const ai = new GoogleGenAI({ apiKey: process.env.API_KEY || '' });

    // Format history for Gemini
    const contents = messages.map(msg => {
      const parts: any[] = [{ text: msg.content }];
      if (msg.image) {
        parts.unshift({
          inlineData: {
            mimeType: msg.image.mimeType,
            data: msg.image.data
          }
        });
      }
      return {
        role: msg.role,
        parts: parts
      };
    });

    try {
      const response = await ai.models.generateContent({
        model: 'gemini-3-flash-preview',
        contents,
        config: {
          systemInstruction: systemInstruction || "You are OursGPT, a highly intelligent and friendly AI assistant. Be concise, helpful, and professional.",
          temperature: 0.7,
          topP: 0.95,
        }
      });
      return response.text || "Sorry, I couldn't generate a response.";
    } catch (error) {
      console.error("Gemini Chat Error:", error);
      throw error;
    }
  },

  async generateImage(prompt: string, style: ImageStyle): Promise<string> {
    const ai = new GoogleGenAI({ apiKey: process.env.API_KEY || '' });

    const styledPrompt = `Style: ${style}. Subject: ${prompt}. Highly detailed, high resolution, masterpiece quality.`;

    try {
      const response = await ai.models.generateContent({
        model: 'gemini-2.5-flash-image',
        contents: {
          parts: [{ text: styledPrompt }]
        },
        config: {
          imageConfig: {
            aspectRatio: "1:1"
          }
        }
      });

      // Find the image part in the response
      for (const part of response.candidates[0].content.parts) {
        if (part.inlineData) {
          return `data:image/png;base64,${part.inlineData.data}`;
        }
      }

      throw new Error("No image was generated in the response.");
    } catch (error) {
      console.error("Gemini Image Generation Error:", error);
      throw error;
    }
  }
};

import React, { useState, useEffect, useCallback } from 'react';
import { Layout } from './components/Layout';
import { ChatView } from './components/ChatView';
import { ImageGeneratorView } from './components/ImageGeneratorView';
import { About } from './components/About';
import { Privacy } from './components/Privacy';
import { Settings } from './components/Settings';
import { AppRoute, ChatThread, Message } from './types';

const App: React.FC = () => {
  const [currentRoute, setCurrentRoute] = useState<AppRoute>(AppRoute.CHAT);
  const [isDarkMode, setIsDarkMode] = useState<boolean>(() => {
    const saved = localStorage.getItem('theme');
    return saved === 'dark' || (!saved && window.matchMedia('(prefers-color-scheme: dark)').matches);
  });

  const [threads, setThreads] = useState<ChatThread[]>(() => {
    const saved = localStorage.getItem('threads');
    return saved ? JSON.parse(saved) : [];
  });

  const [activeThreadId, setActiveThreadId] = useState<string | null>(() => {
    const saved = localStorage.getItem('activeThreadId');
    return saved || null;
  });

  // Persist theme
  useEffect(() => {
    if (isDarkMode) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('theme', 'dark');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('theme', 'light');
    }
  }, [isDarkMode]);

  // Persist threads
  useEffect(() => {
    localStorage.setItem('threads', JSON.stringify(threads));
  }, [threads]);

  useEffect(() => {
    if (activeThreadId) {
      localStorage.setItem('activeThreadId', activeThreadId);
    } else {
      localStorage.removeItem('activeThreadId');
    }
  }, [activeThreadId]);

  const toggleTheme = () => setIsDarkMode(prev => !prev);

  const handleNewChat = () => {
    const newId = Date.now().toString();
    const newThread: ChatThread = {
      id: newId,
      title: 'New Chat',
      messages: [],
      updatedAt: Date.now()
    };
    setThreads(prev => [newThread, ...prev]);
    setActiveThreadId(newId);
    setCurrentRoute(AppRoute.CHAT);
  };

  const updateThreadMessages = (id: string, messages: Message[]) => {
    setThreads(prev => prev.map(t => {
      if (t.id === id) {
        const title = messages[0]?.content.slice(0, 30) || 'New Chat';
        return { ...t, messages, title: t.messages.length === 0 ? title : t.title, updatedAt: Date.now() };
      }
      return t;
    }));
  };

  const deleteThread = (id: string) => {
    setThreads(prev => prev.filter(t => t.id !== id));
    if (activeThreadId === id) {
      setActiveThreadId(null);
    }
  };

  const activeThread = threads.find(t => t.id === activeThreadId) || null;

  return (
    <Layout
      currentRoute={currentRoute}
      setCurrentRoute={setCurrentRoute}
      isDarkMode={isDarkMode}
      toggleTheme={toggleTheme}
      threads={threads}
      activeThreadId={activeThreadId}
      setActiveThreadId={setActiveThreadId}
      onNewChat={handleNewChat}
      onDeleteThread={deleteThread}
    >
      {currentRoute === AppRoute.CHAT && (
        <ChatView 
          activeThread={activeThread} 
          onUpdateMessages={(msgs) => activeThreadId && updateThreadMessages(activeThreadId, msgs)}
          onNewChat={handleNewChat}
        />
      )}
      {currentRoute === AppRoute.IMAGE_GEN && <ImageGeneratorView />}
      {currentRoute === AppRoute.ABOUT && <About />}
      {currentRoute === AppRoute.PRIVACY && <Privacy />}
      {currentRoute === AppRoute.SETTINGS && <Settings isDarkMode={isDarkMode} toggleTheme={toggleTheme} onClearHistory={() => setThreads([])} />}
    </Layout>
  );
};

export default App;

import React, { useState } from 'react';
import { Sidebar } from './Sidebar';
import { AppRoute, ChatThread } from '../types';
import { Menu, X, Sun, Moon } from 'lucide-react';

interface LayoutProps {
  children: React.ReactNode;
  currentRoute: AppRoute;
  setCurrentRoute: (route: AppRoute) => void;
  isDarkMode: boolean;
  toggleTheme: () => void;
  threads: ChatThread[];
  activeThreadId: string | null;
  setActiveThreadId: (id: string | null) => void;
  onNewChat: () => void;
  onDeleteThread: (id: string) => void;
}

export const Layout: React.FC<LayoutProps> = ({
  children,
  currentRoute,
  setCurrentRoute,
  isDarkMode,
  toggleTheme,
  threads,
  activeThreadId,
  setActiveThreadId,
  onNewChat,
  onDeleteThread
}) => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  return (
    <div className="flex h-screen overflow-hidden bg-gray-50 dark:bg-zinc-900 transition-colors duration-200">
      {/* Mobile Sidebar Overlay */}
      {isSidebarOpen && (
        <div 
          className="fixed inset-0 z-40 bg-black/50 lg:hidden" 
          onClick={() => setIsSidebarOpen(false)}
        />
      )}

      {/* Sidebar */}
      <aside className={`
        fixed inset-y-0 left-0 z-50 w-72 transform transition-transform duration-300 ease-in-out lg:relative lg:translate-x-0
        ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'}
      `}>
        <Sidebar 
          currentRoute={currentRoute}
          setCurrentRoute={(r) => { setCurrentRoute(r); setIsSidebarOpen(false); }}
          threads={threads}
          activeThreadId={activeThreadId}
          setActiveThreadId={(id) => { setActiveThreadId(id); setIsSidebarOpen(false); setCurrentRoute(AppRoute.CHAT); }}
          onNewChat={onNewChat}
          onDeleteThread={onDeleteThread}
        />
      </aside>

      {/* Main Content */}
      <div className="flex flex-col flex-1 min-w-0 relative">
        {/* Header */}
        <header className="flex items-center justify-between h-14 px-4 border-b border-gray-200 dark:border-zinc-800 bg-white/80 dark:bg-zinc-900/80 backdrop-blur-sm sticky top-0 z-30">
          <div className="flex items-center gap-3">
            <button 
              onClick={() => setIsSidebarOpen(true)}
              className="p-2 -ml-2 rounded-lg hover:bg-gray-100 dark:hover:bg-zinc-800 lg:hidden"
            >
              <Menu size={20} />
            </button>
            <span className="font-bold text-lg tracking-tight bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">
              OursGPT
            </span>
          </div>

          <div className="flex items-center gap-2">
            <button 
              onClick={toggleTheme}
              className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-zinc-800 text-gray-600 dark:text-zinc-400"
            >
              {isDarkMode ? <Sun size={20} /> : <Moon size={20} />}
            </button>
          </div>
        </header>

        {/* View Content */}
        <main className="flex-1 overflow-hidden">
          {children}
        </main>
      </div>
    </div>
  );
};

