import React, { useState, useRef, useEffect } from 'react';
import { Send, Paperclip, Image as ImageIcon, X, Sparkles } from 'lucide-react';
import { Message, ChatThread } from '../types';
import { MessageBubble } from './MessageBubble';
import { geminiService } from '../services/geminiService';

interface ChatViewProps {
  activeThread: ChatThread | null;
  onUpdateMessages: (messages: Message[]) => void;
  onNewChat: () => void;
}

export const ChatView: React.FC<ChatViewProps> = ({ activeThread, onUpdateMessages, onNewChat }) => {
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [attachedImage, setAttachedImage] = useState<{ data: string, mimeType: string } | null>(null);
  const scrollRef = useRef<HTMLDivElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const scrollToBottom = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  };

  useEffect(() => {
    scrollToBottom();
  }, [activeThread?.messages, isLoading]);

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        const base64Data = event.target?.result as string;
        const data = base64Data.split(',')[1];
        setAttachedImage({ data, mimeType: file.type });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSend = async () => {
    if ((!input.trim() && !attachedImage) || isLoading) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      role: 'user',
      content: input,
      timestamp: Date.now(),
      image: attachedImage || undefined
    };

    const updatedMessages = [...(activeThread?.messages || []), userMessage];

    // If no active thread, start one
    if (!activeThread) {
      onNewChat();
      // Wait for thread creation happens in parent
      return;
    }

    onUpdateMessages(updatedMessages);
    setInput('');
    setAttachedImage(null);
    setIsLoading(true);

    try {
      const response = await geminiService.chat(updatedMessages);
      const aiMessage: Message = {
        id: (Date.now() + 1).toString(),
        role: 'model',
        content: response,
        timestamp: Date.now()
      };
      onUpdateMessages([...updatedMessages, aiMessage]);
    } catch (error) {
      const errorMessage: Message = {
        id: (Date.now() + 1).toString(),
        role: 'model',
        content: "I'm sorry, I encountered an error. Please try again.",
        timestamp: Date.now()
      };
      onUpdateMessages([...updatedMessages, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <div className="flex flex-col h-full bg-white dark:bg-zinc-900 relative">
      {/* Welcome Screen */}
      {!activeThread?.messages.length && !isLoading && (
        <div className="flex-1 flex flex-col items-center justify-center p-6 text-center animate-in fade-in duration-500">
          <div className="w-16 h-16 bg-indigo-100 dark:bg-indigo-900/30 rounded-2xl flex items-center justify-center text-indigo-600 mb-6">
            <Sparkles size={32} />
          </div>
          <h2 className="text-2xl font-bold mb-2">Welcome to OursGPT</h2>
          <p className="text-gray-500 dark:text-zinc-400 max-w-md mb-8">
            How can I help you today? I can assist with writing, analysis, or discussing your images.
          </p>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-3 w-full max-w-lg">
            {[
              "Explain quantum physics like I'm five",
              "Write a short story about a time traveler",
              "How do I cook a perfect risotto?",
              "Generate a workout plan for a beginner"
            ].map((text, i) => (
              <button
                key={i}
                onClick={() => setInput(text)}
                className="p-4 text-sm text-left border border-gray-200 dark:border-zinc-800 rounded-xl hover:bg-gray-50 dark:hover:bg-zinc-800 transition-colors"
              >
                {text}
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Messages */}
      {activeThread?.messages.length || isLoading ? (
        <div ref={scrollRef} className="flex-1 overflow-y-auto custom-scrollbar p-4 md:p-6 space-y-6">
          <div className="max-w-3xl mx-auto w-full space-y-6">
            {activeThread?.messages.map(msg => (
              <MessageBubble key={msg.id} message={msg} />
            ))}
            {isLoading && (
              <div className="flex justify-start">
                <div className="max-w-[85%] bg-gray-100 dark:bg-zinc-800 rounded-2xl p-4 text-gray-700 dark:text-zinc-300">
                  <div className="typing-indicator flex gap-1">
                    <span></span><span></span><span></span>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      ) : null}

      {/* Input Area */}
      <div className="p-4 md:p-6 border-t border-gray-100 dark:border-zinc-800 bg-white dark:bg-zinc-900">
        <div className="max-w-3xl mx-auto relative">
          {attachedImage && (
            <div className="mb-3 relative inline-block">
              <img 
                src={`data:${attachedImage.mimeType};base64,${attachedImage.data}`} 
                className="h-20 w-20 object-cover rounded-xl border border-gray-200 dark:border-zinc-700"
                alt="Upload preview"
              />
              <button 
                onClick={() => setAttachedImage(null)}
                className="absolute -top-2 -right-2 p-1 bg-red-500 text-white rounded-full shadow-md"
              >
                <X size={12} />
              </button>
            </div>
          )}

          <div className="relative group">
            <textarea
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder="Message OursGPT..."
              className="w-full pl-12 pr-12 py-4 bg-gray-50 dark:bg-zinc-800 border border-transparent focus:border-indigo-500/30 focus:bg-white dark:focus:bg-zinc-800 rounded-2xl resize-none min-h-[56px] max-h-48 custom-scrollbar transition-all"
              rows={1}
            />

            <button
              onClick={() => fileInputRef.current?.click()}
              className="absolute left-3 bottom-3 p-2 text-gray-400 hover:text-indigo-500 transition-colors"
              title="Attach Image"
            >
              <Paperclip size={20} />
            </button>

            <button
              onClick={handleSend}
              disabled={isLoading || (!input.trim() && !attachedImage)}
              className={`absolute right-3 bottom-3 p-2 rounded-xl transition-all ${
                input.trim() || attachedImage
                ? 'bg-indigo-600 text-white shadow-lg shadow-indigo-500/30 active:scale-95'
                : 'text-gray-300 dark:text-zinc-600 cursor-not-allowed'
              }`}
            >
              <Send size={20} />
            </button>

            <input 
              type="file" 
              ref={fileInputRef}
              onChange={handleImageUpload}
              accept="image/*"
              className="hidden"
            />
          </div>
          <p className="mt-2 text-[10px] text-center text-gray-400 dark:text-zinc-600">
            OursGPT can make mistakes. Verify important information.
          </p>
        </div>
      </div>
    </div>
  );
};

import React from 'react';
import { Message } from '../types';
import { User, Sparkles } from 'lucide-react';

interface MessageBubbleProps {
  message: Message;
}

export const MessageBubble: React.FC<MessageBubbleProps> = ({ message }) => {
  const isUser = message.role === 'user';

  return (
    <div className={`flex w-full ${isUser ? 'justify-end' : 'justify-start'} animate-in slide-in-from-bottom-2 duration-300`}>
      <div className={`flex gap-3 max-w-[85%] ${isUser ? 'flex-row-reverse' : ''}`}>
        <div className={`flex-shrink-0 w-8 h-8 rounded-lg flex items-center justify-center ${
          isUser 
          ? 'bg-indigo-100 text-indigo-600' 
          : 'bg-zinc-800 text-zinc-400'
        }`}>
          {isUser ? <User size={16} /> : <Sparkles size={16} />}
        </div>

        <div className="flex flex-col gap-2">
          {message.image && (
            <div className={`overflow-hidden rounded-2xl border border-gray-100 dark:border-zinc-800 ${isUser ? 'ml-auto' : ''}`}>
              <img 
                src={`data:${message.image.mimeType};base64,${message.image.data}`} 
                alt="Attached" 
                className="max-h-60 max-w-full object-cover"
              />
            </div>
          )}

          {message.content && (
            <div className={`px-4 py-3 rounded-2xl whitespace-pre-wrap text-sm leading-relaxed ${
              isUser 
              ? 'bg-indigo-600 text-white rounded-tr-none' 
              : 'bg-gray-100 dark:bg-zinc-800 text-gray-800 dark:text-zinc-200 rounded-tl-none'
            }`}>
              {message.content}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

import React, { useState } from 'react';
