import { GoogleGenAI, GenerateContentResponse } from "@google/genai";
import { Message, ImageStyle } from "../types";

export const geminiService = {
  async chat(messages: Message[], systemInstruction?: string): Promise<string> {
    const ai = new GoogleGenAI({ apiKey: process.env.API_KEY || '' });

    // Format history for Gemini
    const contents = messages.map(msg => {
      const parts: any[] = [{ text: msg.content }];
      if (msg.image) {
        parts.unshift({
          inlineData: {
            mimeType: msg.image.mimeType,
            data: msg.image.data
          }
        });
      }
      return {
        role: msg.role,
        parts: parts
      };
    });

    try {
      const response = await ai.models.generateContent({
        model: 'gemini-3-flash-preview',
        contents,
        config: {
          systemInstruction: systemInstruction || "You are OursGPT, a highly intelligent and friendly AI assistant. ALWAYS respond using Markdown formatting. Use **bold** for emphasis, lists for steps, and clear headings. Be concise, helpful, and professional. If you output code, use markdown code blocks.",
          temperature: 0.7,
          topP: 0.95,
        }
      });
      return response.text || "Sorry, I couldn't generate a response.";
    } catch (error) {
      console.error("Gemini Chat Error:", error);
      throw error;
    }
  },

  async generateImage(prompt: string, style: ImageStyle): Promise<string> {
    const ai = new GoogleGenAI({ apiKey: process.env.API_KEY || '' });

    const styledPrompt = `Style: ${style}. Subject: ${prompt}. Highly detailed, high resolution, masterpiece quality.`;

    try {
      const response = await ai.models.generateContent({
        model: 'gemini-2.5-flash-image',
        contents: {
          parts: [{ text: styledPrompt }]
        },
        config: {
          imageConfig: {
            aspectRatio: "1:1"
          }
        }
      });

      // Find the image part in the response
      for (const part of response.candidates[0].content.parts) {
        if (part.inlineData) {
          return `data:image/png;base64,${part.inlineData.data}`;
        }
      }

      throw new Error("No image was generated in the response.");
    } catch (error) {
      console.error("Gemini Image Generation Error:", error);
      throw error;
    }
  }
};

import React, { useMemo } from 'react';
import { Message } from '../types';
import { User, Sparkles } from 'lucide-react';
import { marked } from 'marked';

interface MessageBubbleProps {
  message: Message;
}

export const MessageBubble: React.FC<MessageBubbleProps> = ({ message }) => {
  const isUser = message.role === 'user';

  // Configure marked for security and performance
  const htmlContent = useMemo(() => {
    if (!message.content) return '';
    try {
      // Configure marked options
      marked.setOptions({
        gfm: true,
        breaks: true,
      });
      return marked.parse(message.content);
    } catch (e) {
      console.error('Markdown parsing failed:', e);
      return message.content;
    }
  }, [message.content]);

  return (
    <div className={`flex w-full ${isUser ? 'justify-end' : 'justify-start'} animate-in slide-in-from-bottom-2 duration-300`}>
      <div className={`flex gap-3 max-w-[85%] ${isUser ? 'flex-row-reverse' : ''}`}>
        <div className={`flex-shrink-0 w-8 h-8 rounded-lg flex items-center justify-center ${
          isUser 
          ? 'bg-indigo-100 text-indigo-600' 
          : 'bg-zinc-800 text-zinc-400'
        }`}>
          {isUser ? <User size={16} /> : <Sparkles size={16} />}
        </div>

        <div className="flex flex-col gap-2">
          {message.image && (
            <div className={`overflow-hidden rounded-2xl border border-gray-100 dark:border-zinc-800 ${isUser ? 'ml-auto' : ''}`}>
              <img 
                src={`data:${message.image.mimeType};base64,${message.image.data}`} 
                alt="Attached" 
                className="max-h-60 max-w-full object-cover"
              />
            </div>
          )}

          {message.content && (
            <div className={`px-4 py-3 rounded-2xl text-sm leading-relaxed markdown-content ${
              isUser 
              ? 'bg-indigo-600 text-white rounded-tr-none' 
              : 'bg-gray-100 dark:bg-zinc-800 text-gray-800 dark:text-zinc-200 rounded-tl-none'
            }`}
              dangerouslySetInnerHTML={{ __html: htmlContent }}
            />
          )}
        </div>
      </div>
    </div>
  );
};

