            </button>
          </div>

          <div className="lg:col-span-7 flex flex-col items-center justify-center bg-gray-50 dark:bg-zinc-950 rounded-3xl border border-dashed border-gray-300 dark:border-zinc-800 min-h-[400px] overflow-hidden relative">
            {isGenerating ? (
              <div className="flex flex-col items-center gap-4 p-8 text-center animate-pulse">
                <div className="w-16 h-16 bg-indigo-500/20 rounded-full flex items-center justify-center text-indigo-500">
                  <Sparkles size={32} />
                </div>
                <p className="text-sm font-medium text-gray-500">AI is dreaming up your image...</p>
              </div>
            ) : currentImage ? (
              <div className="w-full h-full group relative">
                <img src={currentImage} className="w-full h-full object-contain" alt="Generated" />
                <div className="absolute inset-x-0 bottom-0 p-6 bg-gradient-to-t from-black/60 to-transparent opacity-0 group-hover:opacity-100 transition-opacity flex justify-between items-center">
                  <span className="text-white text-sm font-medium px-3 py-1 bg-white/20 backdrop-blur-md rounded-lg">
                    {selectedStyle}
                  </span>
                  <button 
                    onClick={() => downloadImage(currentImage, `oursgpt-${Date.now()}.png`)}
                    className="p-3 bg-white text-indigo-600 rounded-full shadow-lg hover:scale-110 transition-transform"
                  >
                    <Download size={20} />
                  </button>
                </div>
              </div>
            ) : (
              <div className="flex flex-col items-center gap-4 text-gray-400 dark:text-zinc-600">
                <ImageIcon size={64} strokeWidth={1} />
                <p>Your creation will appear here</p>
              </div>
            )}
          </div>
        </div>

        {history.length > 0 && (
          <div className="space-y-6 pt-10 border-t border-gray-100 dark:border-zinc-800">
            <h3 className="text-xl font-bold flex items-center gap-2">
              <Sparkles size={20} className="text-indigo-500" />
              Recent Creations
            </h3>
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              {history.map(item => (
                <div key={item.id} className="group relative aspect-square rounded-2xl overflow-hidden border border-gray-200 dark:border-zinc-800 bg-gray-50 dark:bg-zinc-800 transition-all hover:-translate-y-1 hover:shadow-xl">
                  <img src={item.url} className="w-full h-full object-cover" alt={item.prompt} />
                  <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 transition-opacity p-4 flex flex-col justify-end gap-2">
                    <p className="text-white text-[10px] line-clamp-2">{item.prompt}</p>
                    <button 
                      onClick={() => downloadImage(item.url, `oursgpt-${item.id}.png`)}
                      className="w-full py-1.5 bg-white text-zinc-900 rounded-lg text-xs font-bold hover:bg-gray-100 transition-colors flex items-center justify-center gap-1"
                    >
                      <Download size={14} /> Download
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { Layout } from './components/Layout';
import { ChatView } from './components/ChatView';
import { ImageGeneratorView } from './components/ImageGeneratorView';
import { About } from './components/About';
import { Privacy } from './components/Privacy';
import { Settings } from './components/Settings';
import { AppRoute, ChatThread, Message } from './types';

const App: React.FC = () => {
  const [currentRoute, setCurrentRoute] = useState<AppRoute>(AppRoute.CHAT);

  const [isDarkMode, setIsDarkMode] = useState<boolean>(() => {
    try {
      const saved = localStorage.getItem('theme');
      return saved === 'dark' || (!saved && window.matchMedia('(prefers-color-scheme: dark)').matches);
    } catch (e) {
      return false;
    }
  });

  const [threads, setThreads] = useState<ChatThread[]>(() => {
    try {
      const saved = localStorage.getItem('threads');
      return saved ? JSON.parse(saved) : [];
    } catch (e) {
      console.error("Failed to parse threads from localStorage", e);
      return [];
    }
  });

  const [activeThreadId, setActiveThreadId] = useState<string | null>(() => {
    try {
      return localStorage.getItem('activeThreadId');
    } catch (e) {
      return null;
    }
  });

  // Persist theme
  useEffect(() => {
    if (isDarkMode) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('theme', 'dark');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('theme', 'light');
    }
  }, [isDarkMode]);

  // Persist threads
  useEffect(() => {
    localStorage.setItem('threads', JSON.stringify(threads));
  }, [threads]);

  useEffect(() => {
    if (activeThreadId) {
      localStorage.setItem('activeThreadId', activeThreadId);
    } else {
      localStorage.removeItem('activeThreadId');
    }
  }, [activeThreadId]);

  const toggleTheme = () => setIsDarkMode(prev => !prev);

  const handleNewChat = () => {
    const newId = Date.now().toString();
    const newThread: ChatThread = {
      id: newId,
      title: 'New Chat',
      messages: [],
      updatedAt: Date.now()
    };
    setThreads(prev => [newThread, ...prev]);
    setActiveThreadId(newId);
    setCurrentRoute(AppRoute.CHAT);
  };

  const updateThreadMessages = (id: string, messages: Message[]) => {
    setThreads(prev => prev.map(t => {
      if (t.id === id) {
        const title = messages[0]?.content.slice(0, 30) || 'New Chat';
        return { ...t, messages, title: t.messages.length === 0 ? title : t.title, updatedAt: Date.now() };
      }
      return t;
    }));
  };

  const deleteThread = (id: string) => {
    setThreads(prev => prev.filter(t => t.id !== id));
    if (activeThreadId === id) {
      setActiveThreadId(null);
    }
  };

  const activeThread = threads.find(t => t.id === activeThreadId) || null;

  return (
    <Layout
      currentRoute={currentRoute}
      setCurrentRoute={setCurrentRoute}
      isDarkMode={isDarkMode}
      toggleTheme={toggleTheme}
      threads={threads}
      activeThreadId={activeThreadId}
      setActiveThreadId={setActiveThreadId}
      onNewChat={handleNewChat}
      onDeleteThread={deleteThread}
    >
      {currentRoute === AppRoute.CHAT && (
        <ChatView 
          activeThread={activeThread} 
          onUpdateMessages={(msgs) => activeThreadId && updateThreadMessages(activeThreadId, msgs)}
          onNewChat={handleNewChat}
        />
      )}
      {currentRoute === AppRoute.IMAGE_GEN && <ImageGeneratorView />}
      {currentRoute === AppRoute.ABOUT && <About />}
      {currentRoute === AppRoute.PRIVACY && <Privacy />}
      {currentRoute === AppRoute.SETTINGS && (
        <Settings 
          isDarkMode={isDarkMode} 
          toggleTheme={toggleTheme} 
          onClearHistory={() => {
            setThreads([]);
            localStorage.removeItem('threads');
            localStorage.removeItem('activeThreadId');
            localStorage.removeItem('image_history');
          }} 
        />
      )}
    </Layout>
  );
};

export default App;

import React, { useState, useRef, useEffect } from 'react';
import { Send, Paperclip, X, Sparkles } from 'lucide-react';
import { Message, ChatThread } from '../types';
import { MessageBubble } from './MessageBubble';
import { geminiService } from '../services/geminiService';

interface ChatViewProps {
  activeThread: ChatThread | null;
  onUpdateMessages: (messages: Message[]) => void;
  onNewChat: () => void;
}

export const ChatView: React.FC<ChatViewProps> = ({ activeThread, onUpdateMessages, onNewChat }) => {
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [attachedImage, setAttachedImage] = useState<{ data: string, mimeType: string } | null>(null);
  const scrollRef = useRef<HTMLDivElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const scrollToBottom = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  };

  useEffect(() => {
    scrollToBottom();
  }, [activeThread?.messages, isLoading]);

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        const base64Data = event.target?.result as string;
        const data = base64Data.split(',')[1];
        setAttachedImage({ data, mimeType: file.type });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSend = async () => {
    if ((!input.trim() && !attachedImage) || isLoading) return;

    // If no active thread, trigger creation
    if (!activeThread) {
      onNewChat();
      // We rely on the parent state update to provide the thread.
      // For immediate response, we can't easily 'wait' for props in the same function call.
      // However, App.tsx handles this by setting activeThreadId. 
      // The user will see a new chat start.
    }

    const userMessage: Message = {
      id: Date.now().toString(),
      role: 'user',
      content: input,
      timestamp: Date.now(),
      image: attachedImage || undefined
    };

    const currentMessages = activeThread?.messages || [];
    const updatedMessages = [...currentMessages, userMessage];

    onUpdateMessages(updatedMessages);

    const prevInput = input;
    const prevImage = attachedImage;

    setInput('');
    setAttachedImage(null);
    setIsLoading(true);

    try {
      const response = await geminiService.chat(updatedMessages);
      const aiMessage: Message = {
        id: (Date.now() + 1).toString(),
        role: 'model',
        content: response,
        timestamp: Date.now()
      };
      onUpdateMessages([...updatedMessages, aiMessage]);
    } catch (error: any) {
      console.error("Chat sending error:", error);
      setInput(prevInput);
      setAttachedImage(prevImage);

      const errorText = error?.message || "Internal Service Error";
      const errorMessage: Message = {
        id: (Date.now() + 1).toString(),
        role: 'model',
        content: `**Error:** I encountered an issue processing your request (${errorText}). Please ensure your API key is configured correctly.`,
        timestamp: Date.now()
      };
      onUpdateMessages([...updatedMessages, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <div className="flex flex-col h-full bg-white dark:bg-zinc-900 relative">
      {!activeThread?.messages.length && !isLoading && (
        <div className="flex-1 flex flex-col items-center justify-center p-6 text-center animate-in fade-in duration-700">
          <div className="w-20 h-20 bg-indigo-50 dark:bg-indigo-950 rounded-3xl flex items-center justify-center text-indigo-600 dark:text-indigo-400 mb-8 shadow-inner">
            <Sparkles size={40} />
          </div>
          <h2 className="text-3xl font-bold mb-3 tracking-tight">How can I help you today?</h2>
          <p className="text-gray-500 dark:text-zinc-400 max-w-md mb-10 text-lg">
            Experience the power of OursGPT for writing, coding, or exploring ideas.
          </p>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 w-full max-w-2xl px-4">
            {[
              "Explain quantum physics like I'm five",
              "Write a short story about a time traveler",
              "What's the best way to learn React?",
              "Help me plan a 3-day trip to Tokyo"
            ].map((text, i) => (
              <button
                key={i}
                onClick={() => {
                  setInput(text);
                  document.querySelector('textarea')?.focus();
                }}
                className="p-5 text-sm text-left border border-gray-200 dark:border-zinc-800 rounded-2xl hover:bg-gray-50 dark:hover:bg-zinc-800 hover:border-indigo-200 dark:hover:border-indigo-900/50 transition-all duration-200 shadow-sm"
              >
                {text}
              </button>
            ))}
          </div>
        </div>
      )}

      {(activeThread?.messages.length || isLoading) ? (
        <div ref={scrollRef} className="flex-1 overflow-y-auto custom-scrollbar p-4 md:p-6 space-y-6">
          <div className="max-w-3xl mx-auto w-full space-y-8">
            {activeThread?.messages.map(msg => (
              <MessageBubble key={msg.id} message={msg} />
            ))}
            {isLoading && (
              <div className="flex justify-start animate-in fade-in slide-in-from-left-2 duration-300">
                <div className="flex gap-3 max-w-[85%]">
                  <div className="flex-shrink-0 w-8 h-8 rounded-lg flex items-center justify-center bg-zinc-800 text-zinc-400">
                    <Sparkles size={16} />
                  </div>
                  <div className="bg-gray-100 dark:bg-zinc-800 rounded-2xl rounded-tl-none px-4 py-3 text-gray-700 dark:text-zinc-300">
                    <div className="typing-indicator flex gap-1">
                      <span></span><span></span><span></span>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      ) : null}

      <div className="p-4 md:p-6 bg-gradient-to-t from-white via-white dark:from-zinc-900 dark:via-zinc-900 to-transparent">
        <div className="max-w-3xl mx-auto relative">
          {attachedImage && (
            <div className="mb-4 relative inline-block group">
              <img 
                src={`data:${attachedImage.mimeType};base64,${attachedImage.data}`} 
                className="h-24 w-24 object-cover rounded-2xl border-2 border-indigo-500 shadow-lg"
                alt="Upload preview"
              />
              <button 
                onClick={() => setAttachedImage(null)}
                className="absolute -top-3 -right-3 p-1.5 bg-red-500 text-white rounded-full shadow-lg hover:bg-red-600 transition-colors"
              >
                <X size={14} />
              </button>
            </div>
          )}

          <div className="relative group">
            <textarea
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder="Message OursGPT..."
              className="w-full pl-12 pr-14 py-4 bg-gray-50 dark:bg-zinc-800 border border-gray-200 dark:border-zinc-700 focus:border-indigo-500/50 focus:bg-white dark:focus:bg-zinc-800 rounded-[24px] resize-none min-h-[64px] max-h-48 custom-scrollbar transition-all shadow-sm focus:shadow-md outline-none"
              rows={1}
            />

            <button
              onClick={() => fileInputRef.current?.click()}
              className="absolute left-4 bottom-[18px] p-1.5 text-gray-400 hover:text-indigo-500 transition-colors"
              title="Attach Image"
            >
              <Paperclip size={20} />
            </button>

            <button
              onClick={handleSend}
              disabled={isLoading || (!input.trim() && !attachedImage)}
              className={`absolute right-3.5 bottom-3 p-2.5 rounded-2xl transition-all duration-200 ${
                input.trim() || attachedImage
                ? 'bg-indigo-600 text-white shadow-lg shadow-indigo-500/30 hover:bg-indigo-700 active:scale-90'
                : 'text-gray-300 dark:text-zinc-600 cursor-not-allowed'
              }`}
            >
              <Send size={20} />
            </button>

            <input 
              type="file" 
              ref={fileInputRef}
              onChange={handleImageUpload}
              accept="image/*"
              className="hidden"
            />
          </div>
          <p className="mt-3 text-[11px] text-center text-gray-400 dark:text-zinc-500">
            OursGPT may provide inaccurate info. Verify its outputs.
          </p>
        </div>
      </div>
    </div>
  );
};

import { defineConfig } from 'vite';
import react from '@vitejs/plugin-react';

export default defineConfig({
  plugins: [react()],
});

import { GoogleGenAI, GenerateContentResponse } from "@google/genai";
import { Message, ImageStyle } from "../types";

export const geminiService = {
  async chat(messages: Message[], systemInstruction?: string): Promise<string> {
    // Integrate the provided API key to resolve the "missing key" error
    const apiKey = process.env.API_KEY || 'AIzaSyCH5mddkQMPOP_ooqJRVohIZgm1SJw2OVA';
    const ai = new GoogleGenAI({ apiKey });

    // Sanitize and format history for Gemini
    const contents: any[] = [];
    let lastRole: string | null = null;

    for (const msg of messages) {
      // Skip error messages from the history
      if (msg.content.startsWith('**Error:**')) continue;

      // Ensure strictly alternating roles (User -> Model -> User -> Model)
      if (msg.role === lastRole) continue;

      const parts: any[] = [];

      // Handle multimodal input if an image is attached
      if (msg.image) {
        parts.push({
          inlineData: {
            mimeType: msg.image.mimeType,
            data: msg.image.data
          }
        });
      }

      // Add text part
      if (msg.content && msg.content.trim()) {
        parts.push({ text: msg.content.trim() });
      }

      // Only add to contents if we have parts to send
      if (parts.length > 0) {
        contents.push({
          role: msg.role,
          parts: parts
        });
        lastRole = msg.role;
      }
    }

    // API constraint: History must end with a user message
    if (contents.length > 0 && contents[contents.length - 1].role !== 'user') {
       contents.pop();
    }

    if (contents.length === 0) {
      return "No valid message content to process.";
    }

    try {
      const response = await ai.models.generateContent({
        model: 'gemini-3-flash-preview',
        contents,
        config: {
          systemInstruction: systemInstruction || "You are OursGPT, a highly intelligent and friendly AI assistant. ALWAYS respond using Markdown formatting. Use **bold** for emphasis, lists for steps, and clear headings. Be concise, helpful, and professional. If you output code, use markdown code blocks.",
          temperature: 0.7,
          topP: 0.95,
        }
      });
      return response.text || "Sorry, I couldn't generate a response.";
    } catch (error: any) {
      console.error("Gemini Chat API Error:", error);
      throw error;
    }
  },

  async generateImage(prompt: string, style: ImageStyle): Promise<string> {
    const apiKey = process.env.API_KEY || 'AIzaSyCH5mddkQMPOP_ooqJRVohIZgm1SJw2OVA';
    const ai = new GoogleGenAI({ apiKey });

    const styledPrompt = `Style: ${style}. Subject: ${prompt}. Highly detailed, high resolution, masterpiece quality.`;

    try {
      const response = await ai.models.generateContent({
        model: 'gemini-2.5-flash-image',
        contents: {
          parts: [{ text: styledPrompt }]
        },
        config: {
          imageConfig: {
            aspectRatio: "1:1"
          }
        }
      });

      // Find the image part in the response
      for (const part of response.candidates?.[0]?.content?.parts || []) {
        if (part.inlineData) {
          return `data:image/png;base64,${part.inlineData.data}`;
        }
      }

      throw new Error("No image data found in response.");
    } catch (error) {
      console.error("Gemini Image Generation Error:", error);
      throw error;
    }
  }
};

