import { GoogleGenAI } from "@google/genai";
import { Message, ImageStyle } from "../types";

export const geminiService = {
  async chat(messages: Message[], systemInstruction?: string): Promise<string> {
    const apiKey = (process.env.API_KEY && process.env.API_KEY !== '') 
      ? process.env.API_KEY 
      : 'AIzaSyCH5mddkQMPOP_ooqJRVohIZgm1SJw2OVA';

    const ai = new GoogleGenAI({ apiKey });

    const contents: any[] = [];
    let lastRole: string | null = null;

    for (const msg of messages) {
      if (msg.content.startsWith('**Error:**')) continue;
      if (msg.role === lastRole) continue;

      const parts: any[] = [];

      if (msg.image) {
        parts.push({
          inlineData: {
            mimeType: msg.image.mimeType,
            data: msg.image.data
          }
        });
      }

      if (msg.content && msg.content.trim()) {
        parts.push({ text: msg.content.trim() });
      }

      if (parts.length > 0) {
        contents.push({
          role: msg.role,
          parts: parts
        });
        lastRole = msg.role;
      }
    }

    if (contents.length > 0 && contents[contents.length - 1].role !== 'user') {
       contents.pop();
    }

    if (contents.length === 0) {
      return "No valid message content to process.";
    }

    try {
      const response = await ai.models.generateContent({
        model: 'gemini-3-flash-preview',
        contents,
        config: {
          systemInstruction: systemInstruction || "You are OursGPT, a highly intelligent and friendly AI assistant. ALWAYS respond using Markdown formatting. Use **bold** for emphasis, lists for steps, and clear headings. Be concise, helpful, and professional. If you output code, use markdown code blocks.",
          temperature: 0.7,
          topP: 0.95,
        }
      });
      return response.text || "Sorry, I couldn't generate a response.";
    } catch (error: any) {
      console.error("Gemini Chat API Error:", error);
      throw error;
    }
  },

  async generateImage(prompt: string, style: ImageStyle): Promise<string> {
    const apiKey = (process.env.API_KEY && process.env.API_KEY !== '') 
      ? process.env.API_KEY 
      : 'AIzaSyCH5mddkQMPOP_ooqJRVohIZgm1SJw2OVA';

    const ai = new GoogleGenAI({ apiKey });

    const stylePrompts: Record<ImageStyle, string> = {
      realistic: "ultra-realistic, photorealistic, 8k resolution, highly detailed, masterfully captured",
      anime: "anime style, vibrant colors, expressive features, clean lines, studio ghibli aesthetic",
      cinematic: "cinematic lighting, dramatic atmosphere, depth of field, movie still, epic composition",
      '3D': "3D render, octane render, stylized 3D, high polish, Pixar aesthetic",
      illustration: "artistic illustration, hand-drawn feel, creative textures, whimsical style"
    };

    const finalPrompt = `A high-quality image of: ${prompt}. ${stylePrompts[style]}. Beautiful, masterpiece.`;

    try {
      const response = await ai.models.generateContent({
        model: 'gemini-2.5-flash-image',
        contents: {
          parts: [{ text: finalPrompt }]
        },
        config: {
          imageConfig: {
            aspectRatio: "1:1"
          }
        }
      });

      for (const part of response.candidates?.[0]?.content?.parts || []) {
        if (part.inlineData) {
          return `data:${part.inlineData.mimeType};base64,${part.inlineData.data}`;
        }
      }

      const textResponse = response.text;
      if (textResponse) {
        throw new Error(`Model returned text instead of image: ${textResponse}`);
      }

      throw new Error("No image data was generated. The request may have been blocked or failed.");
    } catch (error: any) {
      console.error("Gemini Image Generation Error:", error);
      throw error;
    }
  }
};

import React, { useState } from 'react';
import { Sparkles, Download, Wand2, Loader2, Image as ImageIcon, AlertCircle } from 'lucide-react';
import { ImageStyle, GeneratedImage } from '../types';
import { geminiService } from '../services/geminiService';

const STYLES: { id: ImageStyle; name: string; preview: string }[] = [
  { id: 'realistic', name: 'Realistic', preview: 'https://images.unsplash.com/photo-1506744038136-46273834b3fb?w=200&h=200&fit=crop' },
  { id: 'anime', name: 'Anime', preview: 'https://images.unsplash.com/photo-1578632292335-df3abbb0d586?w=200&h=200&fit=crop' },
  { id: 'cinematic', name: 'Cinematic', preview: 'https://images.unsplash.com/photo-1485846234645-a62644f84728?w=200&h=200&fit=crop' },
  { id: '3D', name: '3D Render', preview: 'https://images.unsplash.com/photo-1633356122544-f134324a6cee?w=200&h=200&fit=crop' },
  { id: 'illustration', name: 'Illustration', preview: 'https://images.unsplash.com/photo-1618005182384-a83a8bd57fbe?w=200&h=200&fit=crop' },
];

export const ImageGeneratorView: React.FC = () => {
  const [prompt, setPrompt] = useState('');
  const [selectedStyle, setSelectedStyle] = useState<ImageStyle>('realistic');
  const [isGenerating, setIsGenerating] = useState(false);
  const [errorMessage, setErrorMessage] = useState<string | null>(null);
  const [history, setHistory] = useState<GeneratedImage[]>(() => {
    try {
      const saved = localStorage.getItem('image_history');
      return saved ? JSON.parse(saved) : [];
    } catch (e) {
      console.error("Failed to parse image history", e);
      return [];
    }
  });
  const [currentImage, setCurrentImage] = useState<string | null>(null);

  const handleGenerate = async () => {
    if (!prompt.trim() || isGenerating) return;

    setIsGenerating(true);
    setCurrentImage(null);
    setErrorMessage(null);

    try {
      const imageUrl = await geminiService.generateImage(prompt, selectedStyle);
      setCurrentImage(imageUrl);

      const newGen: GeneratedImage = {
        id: Date.now().toString(),
        url: imageUrl,
        prompt: prompt,
        style: selectedStyle,
        timestamp: Date.now()
      };

      const newHistory = [newGen, ...history];
      setHistory(newHistory);
      localStorage.setItem('image_history', JSON.stringify(newHistory));
    } catch (error: any) {
      console.error("Image generation error:", error);
      setErrorMessage(error?.message || "Failed to generate image. Please check your API key and network connection.");
    } finally {
      setIsGenerating(false);
    }
  };

  const downloadImage = (url: string, fileName: string) => {
    const link = document.createElement('a');
    link.href = url;
    link.download = fileName;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="flex flex-col h-full bg-white dark:bg-zinc-900 overflow-y-auto custom-scrollbar">
      <div className="max-w-5xl mx-auto w-full p-4 md:p-8 space-y-8">
        <div className="space-y-2 text-center md:text-left">
          <h1 className="text-3xl font-bold tracking-tight bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent">Image Generator</h1>
          <p className="text-gray-500 dark:text-zinc-400">Transform your imagination into visuals with OursGPT.</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-12 gap-8">
          <div className="lg:col-span-5 space-y-6">
            <div className="space-y-4">
              <label className="text-sm font-semibold text-gray-700 dark:text-zinc-300 block">Prompt</label>
              <textarea
                value={prompt}
                onChange={(e) => setPrompt(e.target.value)}
                placeholder="Describe what you want to see... (e.g., 'A cyberpunk city with neon lights')"
                className="w-full p-4 bg-gray-50 dark:bg-zinc-800 border border-gray-200 dark:border-zinc-700 rounded-2xl h-40 resize-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all outline-none"
              />
            </div>

            <div className="space-y-4">
              <label className="text-sm font-semibold text-gray-700 dark:text-zinc-300 block">Style</label>
              <div className="grid grid-cols-3 sm:grid-cols-5 gap-2">
                {STYLES.map(style => (
                  <button
                    key={style.id}
                    onClick={() => setSelectedStyle(style.id)}
                    className={`flex flex-col items-center gap-1 group transition-all ${
                      selectedStyle === style.id ? 'scale-105' : 'opacity-70 grayscale hover:grayscale-0 hover:opacity-100'
                    }`}
                  >
                    <div className={`w-full aspect-square rounded-lg overflow-hidden border-2 ${
                      selectedStyle === style.id ? 'border-indigo-600' : 'border-transparent shadow-md'
                    }`}>
                      <img src={style.preview} className="w-full h-full object-cover" alt={style.name} />
                    </div>
                    <span className={`text-[10px] font-medium ${
                      selectedStyle === style.id ? 'text-indigo-600' : 'text-gray-500'
                    }`}>
                      {style.name}
                    </span>
                  </button>
                ))}
              </div>
            </div>

            <button
              onClick={handleGenerate}
              disabled={isGenerating || !prompt.trim()}
              className={`flex items-center justify-center gap-2 w-full py-4 rounded-2xl font-bold transition-all ${
                isGenerating || !prompt.trim()
                ? 'bg-gray-100 dark:bg-zinc-800 text-gray-400 dark:text-zinc-600 cursor-not-allowed'
                : 'bg-indigo-600 text-white shadow-xl shadow-indigo-500/20 hover:bg-indigo-700 active:scale-[0.98]'
              }`}
            >
              {isGenerating ? (
                <><Loader2 className="animate-spin" size={20} /> Generating...</>
              ) : (
                <><Wand2 size={20} /> Generate Artwork</>
              )}
            </button>

            {errorMessage && (
              <div className="p-4 rounded-xl bg-red-50 dark:bg-red-900/20 border border-red-100 dark:border-red-900/30 flex gap-3 text-red-600 dark:text-red-400 text-sm">
                <AlertCircle size={18} className="flex-shrink-0" />
                <p>{errorMessage}</p>
              </div>
            )}
          </div>

          <div className="lg:col-span-7 flex flex-col items-center justify-center bg-gray-50 dark:bg-zinc-950 rounded-3xl border border-dashed border-gray-300 dark:border-zinc-800 min-h-[400px] overflow-hidden relative shadow-inner">
            {isGenerating ? (
              <div className="flex flex-col items-center gap-4 p-8 text-center animate-in fade-in duration-500">
                <div className="w-16 h-16 bg-indigo-500/20 rounded-full flex items-center justify-center text-indigo-500 animate-bounce">
                  <Sparkles size={32} />
                </div>
                <div className="space-y-1">
                  <p className="text-sm font-semibold text-gray-700 dark:text-zinc-300">AI is dreaming up your image...</p>
                  <p className="text-xs text-gray-400">This usually takes about 10-15 seconds</p>
                </div>
              </div>
            ) : currentImage ? (
              <div className="w-full h-full group relative animate-in zoom-in-95 duration-500">
                <img src={currentImage} className="w-full h-full object-contain" alt="Generated" />
                <div className="absolute inset-x-0 bottom-0 p-6 bg-gradient-to-t from-black/60 to-transparent opacity-0 group-hover:opacity-100 transition-opacity flex justify-between items-center">
                  <span className="text-white text-sm font-medium px-3 py-1 bg-white/20 backdrop-blur-md rounded-lg">
                    {selectedStyle}
                  </span>
                  <button 
                    onClick={() => downloadImage(currentImage, `oursgpt-${Date.now()}.png`)}
                    className="p-3 bg-white text-indigo-600 rounded-full shadow-lg hover:scale-110 transition-transform"
                  >
                    <Download size={20} />
                  </button>
                </div>
              </div>
            ) : (
              <div className="flex flex-col items-center gap-4 text-gray-400 dark:text-zinc-600 text-center px-6">
                <div className="p-6 bg-gray-100 dark:bg-zinc-900 rounded-full">
                  <ImageIcon size={64} strokeWidth={1} />
                </div>
                <div>
                  <p className="font-medium">Ready to create?</p>
                  <p className="text-sm opacity-60">Describe your masterpiece and click generate.</p>
