import { GoogleGenAI } from "@google/genai";
import { Message, ImageStyle } from "../types";

/**
 * Gemini Service using the latest @google/genai SDK.
 * All API keys are handled via process.env.API_KEY as per environment standards.
 */
export const geminiService = {
  async chat(messages: Message[], systemInstruction?: string): Promise<string> {
    // API key must be obtained exclusively from the environment variable
    const apiKey = process.env.API_KEY;
    if (!apiKey) {
      throw new Error("API key is missing in the environment.");
    }

    const ai = new GoogleGenAI({ apiKey });

    // Format history for Gemini ensuring strictly alternating roles (User -> Model)
    const contents: any[] = [];
    let lastRole: string | null = null;

    for (const msg of messages) {
      // Filter out error messages from history and enforce sequence
      if (msg.content.startsWith('**Error:**')) continue;
      if (msg.role === lastRole) continue;

      const parts: any[] = [];

      // Handle Image Input
      if (msg.image) {
        parts.push({
          inlineData: {
            mimeType: msg.image.mimeType,
            data: msg.image.data
          }
        });
      }

      // Handle Text Input
      if (msg.content && msg.content.trim()) {
        parts.push({ text: msg.content.trim() });
      }

      if (parts.length > 0) {
        contents.push({
          role: msg.role,
          parts: parts
        });
        lastRole = msg.role;
      }
    }

    // Ensure the conversation ends with a user message for the API call
    if (contents.length > 0 && contents[contents.length - 1].role !== 'user') {
       contents.pop();
    }

    if (contents.length === 0) {
      return "Hello! How can I help you today?";
    }

    try {
      const response = await ai.models.generateContent({
        model: 'gemini-3-flash-preview',
        contents,
        config: {
          systemInstruction: systemInstruction || "You are OursGPT, a highly intelligent and friendly AI assistant. Use Markdown for all formatting. Be concise, professional, and helpful.",
          temperature: 0.7,
          topP: 0.95,
        }
      });
      return response.text || "I'm sorry, I couldn't process that request.";
    } catch (error: any) {
      console.error("Gemini Chat API Error:", error);
      throw error;
    }
  },

  async generateImage(prompt: string, style: ImageStyle): Promise<string> {
    const apiKey = process.env.API_KEY;
    if (!apiKey) {
      throw new Error("API key is missing in the environment.");
    }

    const ai = new GoogleGenAI({ apiKey });

    const styleModifiers: Record<ImageStyle, string> = {
      realistic: "photorealistic, hyper-detailed, 8k, professional photography",
      anime: "modern anime style, vibrant, high quality digital art",
      cinematic: "cinematic lighting, dramatic composition, movie still quality",
      '3D': "octane render, 3D masterpiece, stylized, high-fidelity",
      illustration: "artistic digital illustration, creative textures"
    };

    const enhancedPrompt = `${prompt}. Style: ${styleModifiers[style]}. Masterpiece quality, high resolution.`;

    try {
      const response = await ai.models.generateContent({
        model: 'gemini-2.5-flash-image',
        contents: {
          parts: [{ text: enhancedPrompt }]
        },
        config: {
          imageConfig: {
            aspectRatio: "1:1"
          }
        }
      });

      // Extract image data from the response parts
      const parts = response.candidates?.[0]?.content?.parts || [];
      for (const part of parts) {
        if (part.inlineData) {
          return `data:${part.inlineData.mimeType};base64,${part.inlineData.data}`;
        }
      }

      throw new Error("The model did not return an image. It may have returned text or failed to generate.");
    } catch (error: any) {
      console.error("Gemini Image API Error:", error);
      throw error;
    }
  }
};

