import React, { useState, useEffect } from 'react';
import { Layout } from './components/Layout';
import { ChatView } from './components/ChatView';
import { ImageGeneratorView } from './components/ImageGeneratorView';
import { About } from './components/About';
import { Privacy } from './components/Privacy';
import { Settings } from './components/Settings';
import { AppRoute, ChatThread, Message } from './types';
import { Key, Sparkles, ShieldCheck, Loader2 } from 'lucide-react';

const App: React.FC = () => {
  const [currentRoute, setCurrentRoute] = useState<AppRoute>(AppRoute.CHAT);
  const [hasKey, setHasKey] = useState<boolean | null>(null);

  const [isDarkMode, setIsDarkMode] = useState<boolean>(() => {
    try {
      const saved = localStorage.getItem('theme');
      return saved === 'dark' || (!saved && window.matchMedia('(prefers-color-scheme: dark)').matches);
    } catch (e) {
      return false;
    }
  });

  const [threads, setThreads] = useState<ChatThread[]>(() => {
    try {
      const saved = localStorage.getItem('threads');
      return saved ? JSON.parse(saved) : [];
    } catch (e) {
      return [];
    }
  });

  const [activeThreadId, setActiveThreadId] = useState<string | null>(() => {
    try {
      return localStorage.getItem('activeThreadId');
    } catch (e) {
      return null;
    }
  });

  // Check for API Key selection on mount
  useEffect(() => {
    const checkKey = async () => {
      if (window.aistudio) {
        const selected = await window.aistudio.hasSelectedApiKey();
        setHasKey(selected);
      } else {
        // Fallback for non-AI Studio environments
        setHasKey(true);
      }
    };
    checkKey();
  }, []);

  useEffect(() => {
    if (isDarkMode) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('theme', 'dark');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('theme', 'light');
    }
  }, [isDarkMode]);

  useEffect(() => {
    localStorage.setItem('threads', JSON.stringify(threads));
  }, [threads]);

  useEffect(() => {
    if (activeThreadId) {
      localStorage.setItem('activeThreadId', activeThreadId);
    } else {
      localStorage.removeItem('activeThreadId');
    }
  }, [activeThreadId]);

  const toggleTheme = () => setIsDarkMode(prev => !prev);

  const handleSelectKey = async () => {
    if (window.aistudio) {
      await window.aistudio.openSelectKey();
      // Assume success as per guidelines to avoid race condition
      setHasKey(true);
    }
  };

  const handleNewChat = () => {
    const newId = Date.now().toString();
    const newThread: ChatThread = {
      id: newId,
      title: 'New Chat',
      messages: [],
      updatedAt: Date.now()
    };
    setThreads(prev => [newThread, ...prev]);
    setActiveThreadId(newId);
    setCurrentRoute(AppRoute.CHAT);
  };

  const updateThreadMessages = (id: string, messages: Message[]) => {
    setThreads(prev => prev.map(t => {
      if (t.id === id) {
        const title = messages[0]?.content.slice(0, 30) || 'New Chat';
        return { ...t, messages, title: t.messages.length === 0 ? title : t.title, updatedAt: Date.now() };
      }
      return t;
    }));
  };

  const deleteThread = (id: string) => {
    setThreads(prev => prev.filter(t => t.id !== id));
    if (activeThreadId === id) {
      setActiveThreadId(null);
    }
  };

  // Entry guard screen
  if (hasKey === false) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center bg-gray-50 dark:bg-zinc-950 p-6">
        <div className="max-w-md w-full bg-white dark:bg-zinc-900 rounded-[32px] p-8 shadow-2xl border border-gray-100 dark:border-zinc-800 text-center space-y-8 animate-in zoom-in-95 duration-500">
          <div className="w-20 h-20 bg-indigo-600 rounded-3xl flex items-center justify-center text-white mx-auto shadow-xl shadow-indigo-500/20">
            <Sparkles size={40} />
          </div>
          <div className="space-y-3">
            <h1 className="text-3xl font-bold tracking-tight">Welcome to OursGPT</h1>
            <p className="text-gray-500 dark:text-zinc-400">
              To access high-performance Pro models for chat and images, please select your API key.
            </p>
          </div>
          <div className="space-y-4">
            <button
              onClick={handleSelectKey}
              className="w-full py-4 bg-indigo-600 hover:bg-indigo-700 text-white rounded-2xl font-bold transition-all shadow-lg flex items-center justify-center gap-3 active:scale-95"
            >
              <Key size={20} />
              Connect API Key
            </button>
            <a 
              href="https://ai.google.dev/gemini-api/docs/billing" 
              target="_blank" 
              rel="noopener noreferrer"
              className="block text-xs text-indigo-500 hover:underline"
            >
              Learn about billing and paid projects
            </a>
          </div>
          <div className="pt-4 flex items-center justify-center gap-2 text-xs text-gray-400">
            <ShieldCheck size={14} />
            <span>Secure connection via Google AI Studio</span>
          </div>
        </div>
      </div>
    );
  }

  if (hasKey === null) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-zinc-950">
        <Loader2 className="animate-spin text-indigo-500" size={32} />
      </div>
    );
  }

  const activeThread = threads.find(t => t.id === activeThreadId) || null;

  return (
    <Layout
      currentRoute={currentRoute}
      setCurrentRoute={setCurrentRoute}
      isDarkMode={isDarkMode}
      toggleTheme={toggleTheme}
      threads={threads}
      activeThreadId={activeThreadId}
      setActiveThreadId={setActiveThreadId}
      onNewChat={handleNewChat}
      onDeleteThread={deleteThread}
    >
      {currentRoute === AppRoute.CHAT && (
        <ChatView 
          activeThread={activeThread} 
          onUpdateMessages={(msgs) => activeThreadId && updateThreadMessages(activeThreadId, msgs)}
          onNewChat={handleNewChat}
        />
      )}
      {currentRoute === AppRoute.IMAGE_GEN && <ImageGeneratorView />}
      {currentRoute === AppRoute.ABOUT && <About />}
      {currentRoute === AppRoute.PRIVACY && <Privacy />}
      {currentRoute === AppRoute.SETTINGS && (
        <Settings 
          isDarkMode={isDarkMode} 
          toggleTheme={toggleTheme} 
          onClearHistory={() => {
            setThreads([]);
            localStorage.removeItem('threads');
            localStorage.removeItem('activeThreadId');
            localStorage.removeItem('image_history');
          }} 
        />
      )}
    </Layout>
  );
};

export default App;

import { GoogleGenAI } from "@google/genai";
import { Message, ImageStyle } from "../types";

/**
 * Gemini Service using the latest @google/genai SDK.
 * Optimized for Flash models to ensure high compatibility and low latency.
 */
export const geminiService = {
  async chat(messages: Message[], systemInstruction?: string): Promise<string> {
    // MUST use process.env.API_KEY exclusively.
    const apiKey = process.env.API_KEY as string;

    // Create instance right before call to ensure latest key is used.
    const ai = new GoogleGenAI({ apiKey });

    const contents: any[] = [];
    let lastRole: string | null = null;

    for (const msg of messages) {
      if (msg.content.startsWith('**Error:**')) continue;
      if (msg.role === lastRole) continue;

      const parts: any[] = [];

      if (msg.image) {
        parts.push({
          inlineData: {
            mimeType: msg.image.mimeType,
            data: msg.image.data
          }
        });
      }

      if (msg.content && msg.content.trim()) {
        parts.push({ text: msg.content.trim() });
      }

      if (parts.length > 0) {
        contents.push({
          role: msg.role,
          parts: parts
        });
        lastRole = msg.role;
      }
    }

    // Ensure last message is from user
    if (contents.length > 0 && contents[contents.length - 1].role !== 'user') {
       contents.pop();
    }

    if (contents.length === 0) {
      return "Hello! I'm OursGPT. How can I assist you today?";
    }

    try {
      const response = await ai.models.generateContent({
        // Using Gemini 3 Flash for maximum compatibility and speed
        model: 'gemini-3-flash-preview',
        contents,
        config: {
          systemInstruction: systemInstruction || "You are OursGPT, a highly intelligent and friendly AI assistant. Use Markdown for all formatting. Be concise, professional, and helpful.",
          temperature: 0.7,
          topP: 0.95,
        }
      });
      return response.text || "I'm sorry, I couldn't process that request.";
    } catch (error: any) {
      console.error("Gemini Chat API Error:", error);
      // Handle key issues or permission errors
      if (error?.message?.includes("permission") || error?.message?.includes("403")) {
        console.warn("Permission denied. Check if the Gemini API is enabled for this project.");
      }
      throw error;
    }
  },

  async generateImage(prompt: string, style: ImageStyle): Promise<string> {
    const apiKey = process.env.API_KEY as string;
    const ai = new GoogleGenAI({ apiKey });

    const styleModifiers: Record<ImageStyle, string> = {
      realistic: "photorealistic, hyper-detailed, professional photography",
      anime: "modern anime style, vibrant digital art",
      cinematic: "cinematic lighting, dramatic composition",
      '3D': "octane render, 3D stylized masterpiece",
      illustration: "artistic digital illustration"
    };

    const enhancedPrompt = `${prompt}. Style: ${styleModifiers[style]}. High quality, high resolution.`;

    try {
      // Using gemini-2.5-flash-image for standard image generation tasks
      const response = await ai.models.generateContent({
        model: 'gemini-2.5-flash-image',
        contents: {
          parts: [{ text: enhancedPrompt }]
        },
        config: {
          imageConfig: {
            aspectRatio: "1:1",
            // Note: imageSize is only for gemini-3-pro-image-preview
          }
        }
      });

      if (response.candidates && response.candidates[0].content.parts) {
        for (const part of response.candidates[0].content.parts) {
          if (part.inlineData) {
            return `data:${part.inlineData.mimeType};base64,${part.inlineData.data}`;
          }
        }
      }

      throw new Error("No image data found in response.");
    } catch (error: any) {
      console.error("Gemini Image API Error:", error);
      throw error;
    }
  }
};

import { GoogleGenAI } from "@google/genai";
import { Message, ImageStyle } from "../types";

/**
 * Gemini Service using the latest @google/genai SDK.
 * Optimized for Gemini 3 Pro models to provide high-quality text and image generation.
 */
export const geminiService = {
  async chat(messages: Message[], systemInstruction?: string): Promise<string> {
    // MUST use process.env.API_KEY exclusively.
    const apiKey = process.env.API_KEY as string;

    // Create instance right before call to ensure the latest selected key is used.
    const ai = new GoogleGenAI({ apiKey });

    const contents: any[] = [];
    let lastRole: string | null = null;

    for (const msg of messages) {
      if (msg.content.startsWith('**Error:**')) continue;
      // Filter out empty messages and ensure alternating roles
      if (!msg.content && !msg.image) continue;
      if (msg.role === lastRole) continue;

      const parts: any[] = [];

      if (msg.image) {
        parts.push({
          inlineData: {
            mimeType: msg.image.mimeType,
            data: msg.image.data
          }
        });
      }

      if (msg.content && msg.content.trim()) {
        parts.push({ text: msg.content.trim() });
      }

      if (parts.length > 0) {
        contents.push({
          role: msg.role,
          parts: parts
        });
        lastRole = msg.role;
      }
    }

    // Model expects the last message to be from the 'user'
    if (contents.length > 0 && contents[contents.length - 1].role !== 'user') {
       contents.pop();
    }

    if (contents.length === 0) {
      return "Hello! I'm OursGPT. How can I assist you today?";
    }

    try {
      const response = await ai.models.generateContent({
        // Gemini 3 Pro for high-quality reasoning and coding
        model: 'gemini-3-pro-preview',
        contents,
        config: {
          systemInstruction: systemInstruction || "You are OursGPT, a highly intelligent and friendly AI assistant. Use Markdown for all formatting. Be concise, professional, and helpful.",
          temperature: 0.7,
          topP: 0.95,
        }
      });
      return response.text || "I'm sorry, I couldn't process that request.";
    } catch (error: any) {
      console.error("Gemini Chat API Error:", error);

      // If permission denied, it often means a project isn't set up or model isn't enabled
      if (error?.message?.includes("403") || error?.message?.includes("permission") || error?.message?.includes("not found")) {
        if (window.aistudio) {
          window.aistudio.openSelectKey();
        }
      }
      throw error;
    }
  },

  async generateImage(prompt: string, style: ImageStyle): Promise<string> {
    const apiKey = process.env.API_KEY as string;
    const ai = new GoogleGenAI({ apiKey });

    const styleModifiers: Record<ImageStyle, string> = {
      realistic: "photorealistic, hyper-detailed, 8k, professional photography, highly detailed textures",
      anime: "modern high-quality anime style, vibrant colors, clean lines, digital art masterpiece",
      cinematic: "cinematic lighting, dramatic atmosphere, depth of field, movie still quality",
      '3D': "octane render, 3D stylized masterpiece, high fidelity, trending on artstation",
      illustration: "artistic digital illustration, creative concept art, professional quality"
    };

    const enhancedPrompt = `${prompt}. Style: ${styleModifiers[style]}. Masterpiece, high resolution, stunning visuals.`;

    try {
      // Using gemini-3-pro-image-preview for 1K resolution high-quality images
      const response = await ai.models.generateContent({
        model: 'gemini-3-pro-image-preview',
        contents: {
          parts: [{ text: enhancedPrompt }]
        },
        config: {
          imageConfig: {
            aspectRatio: "1:1",
            imageSize: "1K"
          }
        }
      });

      // Multimodal response: extract the image data from the parts
      if (response.candidates && response.candidates[0].content.parts) {
        for (const part of response.candidates[0].content.parts) {
          if (part.inlineData) {
            return `data:${part.inlineData.mimeType};base64,${part.inlineData.data}`;
          }
        }
      }

      throw new Error("No image data was generated. The request might have been blocked or the prompt was too sensitive.");
    } catch (error: any) {
      console.error("Gemini Image API Error:", error);

      if (error?.message?.includes("403") || error?.message?.includes("not found")) {
        if (window.aistudio) {
          window.aistudio.openSelectKey();
        }
      }
      throw error;
    }
  }
};
